import Link from "next/link";
import Image from "next/image";
import { wixClientServer } from "../app/lib/wixClientServer";
import DOMPurify from "isomorphic-dompurify";
async function ProductList({ catgoryId, limit = 20, searchParams = "" }) {
  const wixClient = await wixClientServer();
  const productQuery = wixClient.products
    .queryProducts()
    .startsWith("name", searchParams?.name || "")
    .hasSome(
      "productType",
      searchParams?.type ? [searchParams?.type] : ["physical", "digital"]
    )
    .gt("priceData.price", searchParams?.min || 0)
    .lt("priceData.price", searchParams?.max || 9999999)
    .eq("collectionIds", catgoryId)
    .limit(limit);
  console.log("searchParams", searchParams);
  // if (searchParams?.sort) {
  //   const [sortType, sortBy] = searchParams.sort.split(" ");
  //   console.log(sortType);
  //   console.log(sortBy);
  //   if (sortType === "asc") {
  //     productQuery.ascending(sortBy);
  //   } else if (sortType === "desc") {
  //     productQuery.descending(sortBy);
  //   }
  // }
  const res = await productQuery.find();
  return (
    <div className="mt-12 flex gap-x-8 gap-y-16 justify-between flex-wrap">
      {res.items.map((product) => {
        return (
          <Link
            href={"/" + product.slug}
            className="w-full flex flex-col gap-4 sm:w-[45%] lg:w-[22%]"
            key={product._id}
          >
            <div className="relative w-full h-80">
              <Image
                src={product.media?.mainMedia?.image?.url}
                alt="product image"
                placeholder="blur"
                blurDataURL="data:image/png;base64,"
                fill
                className="absolute object-cover rounded-md z-10 hover:opacity-0 transition-opacity ease duration-500"
              ></Image>
              {product.media?.items[1] && (
                <Image
                  src={product.media?.items[1]?.image?.url}
                  alt="product image"
                  fill
                  className="absolute object-cover rounded-md "
                ></Image>
              )}
            </div>
            <div className="flex justify-between">
              <span className="font-medium">{product.name}</span>
              <span className="font-semibold">{product.price?.price}$</span>
            </div>
            <div
              className="text-sm text-gray-500 line-clamp-1"
              dangerouslySetInnerHTML={{
                __html: DOMPurify.sanitize(product.description || ""),
              }}
            ></div>
            <button className="w-max rounded-2xl ring-1 ring-primary text-primary py-2 px-4 text-xs hover:bg-primary hover:text-white">
              ADD TO CART
            </button>
          </Link>
        );
      })}
    </div>
  );
}

export default ProductList;
